import "./expense.css";

function Expense(props){
    const month = props.date?.toLocalString( {month:'long'});
    const year = props.date.getFullYear();
    const day = props.date.toLocalString( {day: 'numeric'});;
    return(
        <div className="card">
            <div className="card-body">
                <h3 className="expense-title">{props.title}</h3>
                <div className="expense-price">Rs./ - { props.price}</div>
                
            </div>
            <div className="card-footer">
            <div className="expense-date">
                <div>{props.date.toISOString('en-US')}</div>
                {/* <div>{year}</div>
                <div>{day}</div> */}
                
                
            </div>
            </div>
        </div>
    );
}
export default Expense;
