import React, { useState } from "react";

import './ExpenseForm.css';

const ExpenseForm =()=>{

    const [enterdTitle, setEnteredTitle] = useState('')
    const [enterdAmount, setEnteredAmount] = useState('')
    const [enterdDate, setEnteredDate] = useState('')

    const changeTitle = (event) =>{
        setEnteredTitle(event.target.value)
        console.log(event.target.value)
    }
    const changeAmount = (event) =>{
        setEnteredAmount(event.target.value)
    }
    const changeDate = (event) =>{
        setEnteredDate(event.target.value)
    }

    return(
        <div>
            <form>
                <div className="expense-form_wraper">
                    <div className="form_controls">
                        <label>Title</label>
                        <input type="text" className="fomr_input" onChange={changeTitle} />
                    </div>
                    <div className="form_controls">
                        <label>Amount</label>
                        <input type="number" min="0.01" step="0.01" className="fomr_input" onChange={changeAmount} />
                    </div>
                    <div className="form_controls">
                        <label>Date</label>
                        <input type="date" className="fomr_input" onChange={changeDate} />
                    </div>
                    <div className="form_controls">
                        
                        <button type="submit" className="btn" >Add Expense</button>
                    </div>
                </div>
            </form>
        </div>
    );

}

export default ExpenseForm;
